//        XCTAssertEqual(decodedValues[1] as! [UInt32], encodedValues[1] as! [UInt32])
//        XCTAssertEqual(decodedValues[2] as! Data, encodedValues[2] as! Data)
//        XCTAssertEqual(decodedValues[3] as! Data, encodedValues[3] as! Data)
    }
    
}
